import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";

export class News extends Component {


    constructor(){
        super();
        console.log("hello i am a constructor");
        this.state={
            articles: [],
            loading:false,
            page:1
        }
    }
    async update(){
        this.setState({loading:true})
            let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=b4e23c0c61f146a3acd405de5bdf045e&page=${this.state.page+1}&pageSize=${this.props.pageSize}`;

            let data = await fetch(url);
            let parseData=await data.json();
            this.setState({page:this.state.page+1 ,articles:parseData.articles,loading:false})
    }

     async componentDidMount(){
        //  let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=b4e23c0c61f146a3acd405de5bdf045e&page=1&pageSize=${this.props.pageSize}`;
        //  this.setState({loading:true})
        //  let data = await fetch(url);
        //  let parseData=await data.json();

        //  this.setState({articles:parseData.articles,totalResults:parseData.totalResults,loading:false})


     }
     handleNextClick=async()=>{
        //  console.log("next")

            // this.setState({loading:true})
            // let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=b4e23c0c61f146a3acd405de5bdf045e&page=${this.state.page+1}&pageSize=${this.props.pageSize}`;

            // let data = await fetch(url);
            // let parseData=await data.json();
            // this.setState({page:this.state.page+1 ,articles:parseData.articles,loading:false})


     }
     handlePrevClick=async()=>{
         console.log("prev")
         this.setState({loading:true})
         let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=b4e23c0c61f146a3acd405de5bdf045e&page=${this.state.page-1}&pageSize=${this.props.pageSize}`;
         let data = await fetch(url);
         let parseData=await data.json();
         this.setState({page:this.state.page-1 ,articles:parseData.articles,loading:false})
    }

  render() {
    return (
      <div>
        <div className="container my-3" >
          <h2 className="text-center">NewsMonkey-Top Headlines</h2>

          {/* <div className="text-center">
          <Spinner/>
          </div> */}
           {this.state.loading && <Spinner/>}


          <div className="row my-4 " >
              {!this.state.loading && this.state.articles.map((element)=>{
                   return <div className="col-md-4" key={element.url}>
                   <NewsItem title={element.title?element.title.slice(0,45):""} description={element.description?element.description.slice(0,88):""} author={element.author} publishedAt={element.publishedAt} source={element.source.name} imageUrl={element.urlToImage} newsUrl={element.url} />
                 </div>
              })}


          </div>

        </div>
        <div className="container d-flex justify-content-between">
        <button disabled={this.state.page<=1} type="button" className="btn btn-sm btn-warning" onClick={this.handlePrevClick}>&larr;Preview</button>
        <button disabled={this.state.page+1> Math.ceil(this.state.totalResults/this.props.pageSize)} type="button" className="btn btn-sm btn-warning "onClick={this.handleNextClick}>Next&rarr;</button>
        </div>
      </div>
    );
  }
}

export default News;
